mapper.select:
SELECT s.uuid AS uuid, s.user_name AS userName, s.mobile AS mobile, s.nick_name AS nickName, s.interest AS interest, s.gender AS gender, s.img_url AS imgUrl, s.email AS email, s.regist_time AS registTime FROM zs_user s WHERE 1=1
and s.user_name = #{userName,jdbcType=VARCHAR}
and s.password = #{password,jdbcType=VARCHAR}
